//WAP to find whether two numbers are same or not.
#include<stdio.h>
int main()
{
    int a,b;
    printf("Enter a");
    scanf("%d",&a);
    printf("Enter b");
    scanf("%d",&b);
    if(a == b)
    {
        printf("Both numbers are same");
    }
    else
    {
        printf("both numbers are not same");
    }
    return 0;
}